.description_info {
    background: #F5F5F5;
    .content_info {
        max-width: calc(1224px + 15px * 2);
        padding: 0 15px;
        margin: 0 auto;
        .container_info {
            display: flex;
            .info_about {
                position: relative;
                width: 912px;
                background: #FFFFFF;
                margin-bottom: 64px;
                margin-right: 24px;
                
                h3 {
                    font-family: 'Gilroy';
                    font-style: normal;
                    font-weight: 600;
                    font-size: 24px;
                    line-height: 100%;
                    color: #373A46;
                    padding: 24px 0 8px 24px;
                }
                .num {
                    font-family: 'Gilroy';
                    font-style: normal;
                    font-weight: 500;
                    font-size: 12px;
                    line-height: 100%;
                    color: #808080;
                    padding-left: 24px;
                    padding-bottom: 16px;
                }
                .button_action {
                    position: absolute;
                    top: 28px;
                    left: 870px;
                }
                .line {
                    background: #EBECF0;
                    width: 912px;
                    height: 1px;
                }
                .description_text {
                    display: flex;
                    padding-top: 20px;
                    padding-bottom: 24px;
                    position: relative;
                    p {
                        font-family: 'Gilroy';
                        font-style: normal;
                        font-weight: 600;
                        font-size: 14px;
                        line-height: 110%;
                        color: #373A46;
                        padding-left: 40px;
                    }
                    svg {
                        position: absolute;
                        top: 26px;
                        left: 22px;
                    }
                    
                }
                .list_obj {
                    display: flex;
                    .type {
                        li {
                            list-style-type:none;
                            font-family: 'Gilroy';
                            font-style: normal;
                            font-weight: 500;
                            font-size: 16px;
                            line-height: 110%;
                            color: #808080;
                            padding-bottom: 24px;
                            padding-left: 40px;
                        }
                        
                    }
                    .value {
                        li {
                            list-style-type:none;
                            font-family: 'Gilroy';
                            font-style: normal;
                            font-weight: 500;
                            font-size: 16px;
                            line-height: 110%;
                            color: #373A46;
                            padding-bottom: 24px;
                            padding-left: 64px;
                        }
                    }
                }
                .obj_about {
                    .add_btn {
                        button:first-child {
                            margin-left: 40px;
                            svg {
                                margin-left: 9px;
                            }
                        }
                        button {
                            margin-right: 8px;
                            border: none;
                            font-family: 'Plumb';
                            font-style: normal;
                            font-weight: 400;
                            font-size: 13px;
                            line-height: 15px;
                            color: #000007;
                            padding: 8px 24px 9px 16px;
                            margin-bottom: 40px;
                            text-align: center;
                        }
                        svg {
                            margin-left: 19px;
                        }
                        .plus {
                            position: relative;
                            background: #FFFFFF;
                            border: 1px solid #2B4BFB;
                            border-radius: 2px;
                            width: 32px;
                            height: 32px;
                            svg {
                                position: absolute;
                                top: 8px;
                                left: -4px;
                            }
                        }
                    }
                    .text {
                        font-family: 'Gilroy';
                        font-style: normal;
                        font-weight: 500;
                        font-size: 16px;
                        line-height: 150%;
                        color: #373A46;
                        margin-left: 40px;
                        margin-bottom: 40px;
                        width: 831px;
                        height: 72px;
                    }
                    .files {
                        display: flex;
                        position: relative;
                        margin-bottom: 41px;
                        .pdf {
                            background: #FFFFFF;
                            border: 1px solid #EBECF0;
                            border-radius: 4px;
                            width: 320px;
                            height: 72px;
                            margin-right: 16px;
                            margin-left: 40px;
                            p {
                                font-family: 'Gilroy';
                                font-style: normal;
                                font-weight: 500;
                                font-size: 14px;
                                line-height: 16px;
                                color: #2C6CCB;
                                padding-left: 72px;
                                padding-top: 21px;
                            
                            }
                            svg {
                                position: absolute;
                                top: 13px;
                                left: 64px;
                            }
                        }
                        
                        .link {
                            background: #FFFFFF;
                            border: 1px solid #EBECF0;
                            border-radius: 4px;
                            width: 320px;
                            height: 72px;
                            p {
                                font-family: 'Gilroy';
                                font-style: normal;
                                font-weight: 500;
                                font-size: 14px;
                                line-height: 16px;
                                color: #2C6CCB;
                                padding-left: 72px;
                                padding-top: 21px;
                            
                            }
                            svg {
                                position: absolute;
                                top: 11px;
                                left: 403px;
                            }
                        }
                    }
                }
            }
            .about_company {
                max-width: 288px;
                max-height: 506px;
                background: #FFFFFF;
                .logo {
                    margin-bottom: 40px;
                    margin-left: 24px;
                    margin-top: 24px;
                    
                }
                .text_about_company {
                    padding-left: 24px;
                   
                    .title {
                        font-family: 'Gilroy';
                        font-style: normal;
                        font-weight: 500;
                        font-size: 14px;
                        line-height: 110%;
                        color: #808080;
                        padding-bottom: 8px;
                    }
                    p {
                        font-family: 'Gilroy';
                        font-style: normal;
                        font-weight: 600;
                        font-size: 16px;
                        line-height: 110%;
                        color: #373A46;
                        padding-bottom: 24px;
                        padding-right: 10px;
                    }
                }
               button {
                margin-top: 24px;
                width: 288px;
                height: 64px;
                background: #2B4BFB;
                border-radius: 4px;
                border: none;
                font-family: 'Gilroy';
                font-style: normal;
                font-weight: 500;
                font-size: 14px;
                line-height: 16px;
                color: #FFFFFF;

               }
            }
        }
    }
    
}
